from enigma import eSize, ePoint orgwidth = self.instance.size().width() orgpos = self.instance.position() textsize = self["text"].getSize() if (textsize[0] > 500): textsize = (500, textsize[1]+19) elif (textsize[0] < 250): textsize = (250,textsize[1]+19) else: textsize = (textsize[0]+50,textsize[1]+19)
offset = 0 espacio= 5 if self.type == self.TYPE_YESNO: offset = 25 wsizex = textsize[0] + 50 wsizey = textsize[1] + offset+espacio
wsize = (wsizex, wsizey) posbotonx=wsizex-83-4 posbotony=wsizey-33-4 self.instance.resize(eSize(*wsize)) self["text"].instance.resize(eSize(*textsize)) listsize = (wsizex - 20, 50) self["list"].instance.move(ePoint(posbotonx+12, posbotony+4)) # self["list"].instance.resize(eSize(*listsize)) newwidth = wsize[0] self.instance.move(ePoint(1280 - newwidth-60, 80)) if self.type == self.TYPE_YESNO: self["ErrorPixmap"].instance.setPixmapFromFile("/share/enigma2/basic_retro_HD/flechas-fs8.png") self["ErrorPixmap"].instance.move(ePoint(posbotonx, posbotony)) self["ErrorPixmap"].show() self.list = [ (self.list[0][0].capitalize(), self.list[0][1]), (self.list[1][0].capitalize(), self.list[1][1]) ] self["selectedChoice"].setText(self.list[0][0]) self["list"].setList(self.list)

